const Hello = function () {
  function showName() {
    console.log("Mike");
  }

  function showAge(age) {
    console.log(age);
  }

  function showText(e) {
    console.log(e.target.value);
  }

  return (
    <div>
      <h1>State</h1>
      <h2>컴포넌트의 속성값</h2>
      <button onClick={showName}>Show name</button>
      <button
        onClick={() => {
          showAge(30);
        }}
      >
        Show age
      </button>
      <input type="text" onChange={showText} />
    </div>
  );
};

export default Hello;
